#!/usr/bin/env node
var mongojs=require('mongojs'),dburl=process.env.MONGOHQ_URL||'mongodb://localhost/concertdacote',db=mongojs(dburl,['concerts']);function insertData(data){db.concerts.insert(data,{continueOnError:true},function(err,docs){if(err)console.log('err: '+err+'\n');else console.log('data inserted successfully!\n');});}
function dropCollection(){db.concerts.remove({"_id" : {$not: /.*pij.*/}});}
function ensureIndex(){db.concerts.ensureIndex({latlong:"2d"});}
function closeDatabase(){db.close();console.log('db closed!');}
exports.dropCollection=dropCollection;exports.insertData=insertData;exports.ensureIndex=ensureIndex;exports.closeDatabase=closeDatabase;